"use client";

import { motion } from "framer-motion";
import React, { useState, useEffect } from "react";

export function PoweredBySection() {
  const [isPaused, setIsPaused] = useState(false);
  const [shouldAnimate, setShouldAnimate] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShouldAnimate(false);
    }, 20000); 

    return () => clearTimeout(timer);
  }, []);

  return (
    <section className="py-16 rounded-b-sm bg-neutral-200 mt-20 rounded-t-[100px] dark:bg-neutral-800 overflow-hidden relative">
      <div className="max-w-5xl mx-auto px-6 md:px-8 text-center">
        <motion.h2
          className="text-2xl sm:text-3xl md:text-4xl font-poppins  md:text-start font-bold mb-8 text-neutral-800 dark:text-white"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
        >
          Powered By ðŸ”¥
        </motion.h2>

        <div
          className={`flex justify-center overflow-hidden`}
          onMouseEnter={() => setIsPaused(true)}
          onMouseLeave={() => setIsPaused(false)}
        >
          <motion.div
            className={`flex space-x-8 justify-center text-center items-center md:space-x-16 ${
              shouldAnimate && !isPaused ? "animate-scroll" : ""
            }`}
          >
            {logos.map((logo, index) => (
              <motion.div
                key={index}
                className="grayscale hover:grayscale-0 transition-all duration-300 ease-in-out"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{
                  delay: index * 0.2,
                  duration: 0.8,
                  ease: "easeOut",
                }}
              >
                {logo.src}
              </motion.div>
            ))}
          </motion.div>
        </div>
      </div>

      {/* Carousel Animation */}
      <style jsx>{`
        @keyframes scroll {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(calc(-100% + 100vw));
          }
        }

        .animate-scroll {
          display: flex;
          animation: scroll 20s linear forwards; 
        }
      `}</style>
    </section>
  );
}

